$(document).ready(function(){if($('body').hasClass('home')){$('img.banner[src*="howe-healthy-homepage"]').attr('usemap','#heartMap');}
$('.banner').cycle({fx:'fade',timeout:4500});$('.leftmenu .col2 .inner ul>li>div>ul>li>a').click(function(){if($(this).attr('href')=='#'){$(this).next().toggle('fast');$(this).parent().toggleClass('current-menu-parent');return false;}else{return true;}});if($('.sub-menu #s').val()!=''){$('#searchform label').hide();}
$('.sub-menu #s').focus(function(){if($(this).val()==''){$('#searchform label').hide();}}).blur(function(){if($(this).val()==''){$('#searchform label').show();}});$().piroBox_ext({piro_speed:300,bg_alpha:0.5,piro_scroll:true,slideShow:true,slideSpeed:3,close_all:'.piro_close,.piro_overlay'});if($('#rating').size()>0){ss=$('<span class="stars" />');for(i=1;i<6;i++){ss.append('<a href="#'+i+'" class="on">'+i+'</a>');}
ss.children().bind({mouseover:function(){$(this).add($(this).prevAll()).addClass('on');$(this).nextAll().removeClass('on');$('#rating').val($(this).html());return false;}});$('#rating').hide().after(ss);}});
